add_action('wp_ajax_narrative_lite_fonts_ajax', 'narrative_lite_fonts_ajax_cb');
add_action('wp_ajax_nopriv_narrative_lite_fonts_ajax', 'narrative_lite_fonts_ajax_cb');

if( !function_exists('narrative_lite_fonts_ajax_cb') ):

    // Recommendec Post Ajax Call Function.
    function narrative_lite_fonts_ajax_cb() {

        $family = isset( $_POST['family'] ) ? sanitize_text_field( $_POST['family'] ) : '';
        $arr = narrative_lite_font_array();
        $key = array_search($family, array_column($arr, 'family'));

        $variants = $arr[$key]['variants'];
        if( $variants ){
            $i = 1;
            foreach( $variants as $key => $value ){

                echo "<option value='".$key."'>".$value."</option>";
                $i++;
            }
        }
    }

endif;

if( !function_exists('narrative_lite_fonts_variants') ):

    // Recommendec Post Ajax Call Function.
    function narrative_lite_fonts_variants( $font_family = '' ) {

        $arr = narrative_lite_font_array();
        $key = array_search($font_family, array_column($arr, 'family'));
        $variants_lists = '';
        $key;
        $variants = $arr[$key]['variants'];
        if( $variants ){
            foreach( $variants as $value ){

                $variants_lists .= $value.',';
            }
        }

        return $variants_lists;

    }

endif;

if( !function_exists('narrative_lite_google_fonts') ):

    function narrative_lite_google_fonts(){

        $google_fonts = narrative_lite_font_array();
        foreach ($google_fonts as $key => $value) {
            $google_font[$value['family']] =  $value['family'] ;
        }
        return( $google_font );
    }

endif;

