if( !function_exists( 'narrative_lite_font_url' ) ) :

    function narrative_lite_font_url() {

        $narrative_lite_default = narrative_lite_get_default_theme_options();
        $wedev_tagline_font = get_theme_mod('wedev_tagline_font', $narrative_lite_default['wedev_tagline_font']);
        $wedev_general_font = get_theme_mod('wedev_general_font', $narrative_lite_default['wedev_general_font']);
        $wedev_heading_font = get_theme_mod('wedev_heading_font', $narrative_lite_default['wedev_heading_font']);

        // Fonts Array
        $fonts = array($wedev_tagline_font, $wedev_general_font, $wedev_heading_font);

        // Remove if font repeat
        $fonts = array_unique($fonts);

        // Remove Local Font
        $remove_font_1 = array_search('Georgia', $fonts);
        if (in_array('Georgia', $fonts)) {
            unset($fonts[$remove_font_1]);
        }

        $remove_font_2 = array_search('Helvetica', $fonts);
        if (in_array('Helvetica', $fonts)) {
            unset($fonts[$remove_font_2]);
        }

        $remove_font_3 = array_search('Arial', $fonts);
        if (in_array('Arial', $fonts)) {
            unset($fonts[$remove_font_3]);
        }

        $narrative_lite_fonts = array();

        $font_count = count($fonts);

        $j = 1;
        foreach ($fonts as $font) {

            // Get Font Property
            $fonts_property_list = narrative_lite_fonts_variants( $font );

            if ($fonts_property_list) {

                $fonts_property_list = rtrim($fonts_property_list, ',');
                if ($font_count == $j) {
                    $narrative_lite_fonts[] = $font . ':' . $fonts_property_list . '&display=swap';
                } else {
                    $narrative_lite_fonts[] = $font . ':' . $fonts_property_list;
                }
            } else {

                if ($font_count == $j) {
                    $narrative_lite_fonts[] = $font . '&display=swap';
                } else {
                    $narrative_lite_fonts[] = $font;
                }

            }

            $j++;
        }

        if ($narrative_lite_fonts) {

            $i = 0;
            for ($i = 0; $i < count($narrative_lite_fonts); $i++) {

                if ('off' !== sprintf(_x('on', '%s font: on or off', 'narrative-lite'), $narrative_lite_fonts[$i])) {
                    $fonts[] = $narrative_lite_fonts[$i];
                }

            }

            if ($fonts) {
                $fonts_url = add_query_arg(array(
                    'family' => urldecode(implode('|', $fonts)),
                ), 'https://fonts.googleapis.com/css');
            }

            return esc_url_raw($fonts_url);

        }

    }

endif;